import {
    Sdk,
    SDK_SETTINGS,
    $cartData,
    $cartDispared,
    $initChangeCountry,
    $initSynchronize,
    $initMulticalculate,
    SynchronizeActions
} from "./src";

class App {
    sdk: Sdk = new Sdk(SDK_SETTINGS);

    constructor() {
        this.sdk.init();
        this.subscribes();
    }

    subscribes() {
        $cartData.subscribe((r) => {
            console.log('Cart Sky', r)
        });

        $cartDispared.subscribe((r) => {
            console.log('Cart Shopify', r)
        });

        $initChangeCountry.subscribe((r) => {
            console.log(r);
        });

        $initMulticalculate.subscribe((r) => {
            console.log(r);
        });

        $initSynchronize.subscribe((r) => {
            console.log(r);
        });

        SynchronizeActions.$onAdd.subscribe((r) => {
            console.log('onAdd', r);
        });

        SynchronizeActions.$onDelete.subscribe((r) => {
            console.log('onDelete', r);
        });

        SynchronizeActions.$onUpdate.subscribe((r) => {
            console.log('onUpdate', r);
        });

        SynchronizeActions.$onRemoveCart.subscribe((r) => {
            console.log('onRemoveCart', r);
        });
    }
}

new App();