/**
 *  Clase Abstracta que representa como deberia de implementar un componente HTML.
 *  con los métodos requeridos
 *  @class
 * */
export abstract class ComponentAbstract {
    abstract rawHtml: string;

    constructor() {

    }

    /** obtener el elemento o elementos HTML que se renderizan*/
    abstract getRawHtml(): string;

    /** Método para renderizar en la web el elemento HTML que se ha generado */
    abstract render(): void;
}